import { LockOutlined, MailOutlined } from "@ant-design/icons";
import { Button, Form,  Input, Typography, message} from "antd";
import axios from "axios";
import React, { useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import './Login.css';
import { DashboardContext } from "../../Dashboard/dashContext";
const { Text, Link } = Typography;

const Login = () => {
  const navigate = useNavigate();
  const [messageApi, contextHolder] = message.useMessage();

  const { setDashboardSelectedItem } = useContext(DashboardContext);

  const displayMessage = (messageType, data) => {
    messageApi.open({
      type: messageType,
      content: data?.message,
    });
  };

  const onFinish = async (values) => {
    console.log("Received values of form: ", values);

    const options = {
      method: "POST",
      url: "http://localhost:4000/auth/login",
      data: values,
    };

    try {
      const response = await axios.request(options);
      localStorage.setItem("token")
      console.log(response.data);

      displayMessage("success", {message:"Login Successful"});
      setDashboardSelectedItem("Mobiles");
      navigate("/dashboard");
    } catch (error) {
      displayMessage("error", {message: "Invalid credentials"});

    }
  };

  useEffect(() => {
    setDashboardSelectedItem("Mobiles");
  }, [])

  return (
    <>
       {contextHolder}
      <section className="bg-img">
        <div className="content">
          <header>Sign in</header>
          <Form
            name="login"
            aria-label="login form"
            onFinish={onFinish}
            layout="vertical"
            requiredMark="optional"
          >
            <Form.Item
              name="email"
              label="Email"
              rules={[
                {
                  type: "email",
                  required: true,
                  message: "Please input your Name!",
                },
              ]}
            >
              <Input prefix={<MailOutlined />} placeholder="Email" />
            </Form.Item>
            <Form.Item
              name="password"
              label="Password"
              rules={[
                {
                  required: true,
                  message: "Please input your Password!",
                },
              ]}
            >
              <Input.Password
                prefix={<LockOutlined />}
                type="password"
                placeholder="Password"
              />
            </Form.Item>

            <Form.Item style={{ marginBottom: "0px" }}>
              <Button block="true" type="primary" htmlType="submit">
                Log in
              </Button>
              <div >
                <Text >Don't have an account?</Text>{" "}
                <Link href="/register">Sign up now</Link>
              </div>
            </Form.Item>
          </Form>
        </div>
      </section>
    </>
  );
};

export default Login;
